import React from "react";

import { Typography, Container, Box } from "@mui/material";
import { Button } from "@mui/material";

const bgImg =
    "https://images.pexels.com/photos/17488420/pexels-photo-17488420/free-photo-of-madera-paisaje-agua-apple.jpeg";

export const Intro = () => {
    return (
        <Container
        maxWidth="none"
            sx={{
                padding: { xs: 0, md: 0 },
                display: "grid",
                width: "100%",
                height: "100vh",
                position: "relative",
                }}>

            {/* img */}
            <Box
                sx={{
                    overflow: "hidden",
                    position: "absolute",
                    height: "100%",
                    width: "100%"}}>
                <img
                    src={bgImg}
                    alt="imgHotel"
                    style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                    // sx={{
                    //     top: "0",
                    // }}
                />
            </Box>

            {/* buttons */}
            {/* <Box sx={{
                            width: "100%",
                            top: "50%",
                            display: "flex",
                            justifyContent: "space-between",
                            left: "1rem",
                            position: "absolute",
                            right: "8rem",
                            transform: "translateY(-50%)",
                            zIndex: 2000 }} > 

                            <Box sx={{ color: 'white', }}>
                                <ArrowCircleLeftRoundedIcon fontSize="large"/> 
                            </Box>
                            <Box sx={{ color: 'white', }}>
                                <ArrowCircleRightRoundedIcon fontSize="large"/> 
                            </Box>

                        </Box> */}

            {/* text box */}

            <Box
                sx={{
                    width: "100%",
                            top: "50%",
                            display: "flex",
                            left: "1rem",
                            position: "absolute",
                            zIndex: 2000 ,

                        //height: "33vh",
                    //width: "50%",
                        zIndex: 1,
                    //position: "absolute",
                    //top: 100,
                    //left: 250,
                    //display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "center",
                    alignSelf: "center",
                    gap: "0,8rem",
                    color: "white",
                }}>
                <Typography variant="h3">AURORA MONTAIN </Typography>
                <Typography variant="subtitle2">É muito fácil revelar na FotoRegistro! As fotos podem ser enviadas tanto do seu celular quanto do computador. Faça login no site, escolha suas fotos e receba em casa. </Typography>
                <Button variant="outlined"> Contact us </Button>

            </Box>
        </Container>
    );
};
