import { Typography, Box, Stack, Button } from "@mui/material";
import React, { useState } from "react";

import FormHelperText from "@mui/material/FormHelperText";

import Grid from "@mui/material/Grid";
import Slider from "@mui/material/Slider";
import MuiInput from "@mui/material/Input";
import { BoyRounded } from "@mui/icons-material";
import { styled } from "@mui/material/styles";
import { Container } from "@mui/material";

import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { useRoomContext } from "../../context/RoomProvider";

import dayjs from "dayjs";
import { DemoContainer, DemoItem } from "@mui/x-date-pickers/internals/demo";
import { TextField } from "@mui/material";
import { GifBoxSharp } from "@mui/icons-material";

const Input = styled(MuiInput)`
    width: 42px;
`;

export const SearchBar = ({
    handleAdultSliderChange,
    handleAdultInputChange,
    handleAdultBlur,
    handlekidsSliderChange,
    handlekidsInputChange,
    handlekidsBlur,
    setStartDate,
    setEndDate,
}) => {
    const { adults, kids, handleClick } = useRoomContext();

    return (
        <Container sx={{ backgroundColor: "#858484" }}>
            <Box
                sx={{
                    paddingTop: "4rem",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    gap: "2rem",
                }}
            >
                <Box sx={{ width: 200 }}>
                    <DemoContainer components={["DatePicker"]}>
                        <DemoItem>
                            <DatePicker
                                label="Check in"
                                value={dayjs("2023-08-17")}
                                onChange={(date) => setStartDate(date)}
                            />
                        </DemoItem>
                    </DemoContainer>
                </Box>

                <Box sx={{ width: 200 }}>
                    <DemoContainer components={["DatePicker"]}>
                        <DemoItem>
                            <DatePicker
                                label="Check out"
                                value={dayjs("2023-08-17")}
                                onChange={(date) => setEndDate(date)}
                            />
                        </DemoItem>
                    </DemoContainer>
                </Box>

                <Box sx={{ width: 200 }}>
                    <input
                        type="date"
                        label="Check In"
                        id="start"
                        name="trip-start"
                        value={dayjs("2023-08-17")}
                        onChange={(date) => setStartDate(date)}                        
                        min="2018-01-01"
                        max="2018-12-31"
                    />
                </Box>
                <Box sx={{ width: 200 }}>
                    <TextField
                        type="date"
                        id="start"
                        name="trip-start"
                        value="2018-07-22"
                        min="2018-01-01"
                        max="2018-12-31"
                    />
                </Box>

                <Box sx={{ width: 200 }}>
                    <TextField
                        type="range"
                        id="start"
                        name="trip-start"
                        value="2018-07-22"
                        min="2018-01-01"
                        max="2018-12-31"
                    />
                </Box>

                <Box sx={{ width: 150 }}>
                    <Typography id="input-slider" variant="caption">
                        Adult
                    </Typography>

                    <Grid container spacing={2} alignItems="center">
                        <Grid item>
                            <BoyRounded />
                        </Grid>

                        <Grid item xs>
                            <Slider
                                id="adult"
                                size="small"
                                value={adults ? adults : 0}
                                onChange={handleAdultSliderChange}
                                step={1}
                                min={1}
                                max={5}
                                aria-labelledby="input-slider"
                            />
                        </Grid>

                        <Grid item>
                            <Input
                                value={adults}
                                size="small"
                                onChange={handleAdultInputChange}
                                onBlur={handleAdultBlur}
                                inputProps={{
                                    step: 1,
                                    min: 0,
                                    max: 5,
                                    type: "number",
                                    "aria-labelledby": "input-slider",
                                }}
                            />
                        </Grid>
                    </Grid>
                </Box>

                <Box sx={{ width: 150 }}>
                    <Typography id="input-slider" variant="caption">
                        Niños
                    </Typography>

                    <Grid container spacing={2} alignItems="center">
                        <Grid item>
                            <BoyRounded />
                        </Grid>

                        <Grid item xs>
                            <Slider
                                id="kids"
                                size="small"
                                value={kids ? kids : 0}
                                onChange={handlekidsSliderChange}
                                step={1}
                                min={0}
                                max={5}
                                aria-labelledby="input-slider"
                            />
                        </Grid>

                        <Grid item>
                            <Input
                                value={kids}
                                size="small"
                                onChange={handlekidsInputChange}
                                onBlur={handlekidsBlur}
                                inputProps={{
                                    step: 1,
                                    min: 0,
                                    max: 5,
                                    type: "number",
                                    "aria-labelledby": "input-slider",
                                }}
                            />
                        </Grid>
                    </Grid>
                </Box>

                <Button
                    onClick={handleClick}
                    variant="contained"
                    color="primary"
                >
                    Check Availability
                </Button>
            </Box>
        </Container>
    );
};






