import { useRoomContext } from "../context/RoomProvider";


const { adults, kids } = useRoomContext()


export const searchGuests = [
    {
        id: "adults",
        label: "Adults",
        name: "adult-guests",
        value: adults
    },
    {
        id: "kids",
        label: "Kids",
        name: "kids-guests",
        value: kids
    },
];
