import React from "react";

import AspectRatio from "@mui/joy/AspectRatio";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import IconButton from "@mui/joy/IconButton";
import Typography from "@mui/joy/Typography";
import Favorite from "@mui/icons-material/Favorite";
import CreateNewFolder from "@mui/icons-material/CreateNewFolder";
import { BoyRounded } from "@mui/icons-material";
import { Stack } from "@mui/material";
import { Link } from "react-router-dom";
import { Buttons } from "../button/Buttons";

export const RoomItem = ({ rooms }) => {
    const title = rooms.title;
    const capacity = rooms.capacity;
    const amenities = rooms.amenities;
    const allows = rooms.allows;
    const donts = rooms.donts;
    const description = rooms.description;
    const price = rooms.price;
    const imgUrl = rooms.imgUrl;

    return (
        <Card
            sx={{
                width: 300,
                bgcolor: "initial",
                boxShadow: "none",
                "--Card-padding": "0px",
            }}>
            <AspectRatio ratio="3/4">
                <figure>
                    <img src={imgUrl} loading="lazy" alt={title} />
                </figure>
            </AspectRatio>

            <CardCover
                className="gradient-cover"
                sx={{
                    "&:hover, &:focus-within": {
                        opacity: 1,
                    },
                    opacity: 0,
                    transition: "0.2s ease-in",
                    background:
                        "linear-gradient(0deg, #222223d5 0%, #46474734 100%); ",
                    padding: "1rem",
                }}>
                <Link to={`/room/${title}`}>
                    {/* The first box acts as a container that inherits style from the CardCover */}
                    <Box
                        sx={{
                            height: "100%",
                            display: "flex",
                            flexDirection: "column",
                            gap: "1.5rem",
                        }}
                    >
                        <p className="title-2 ">{title}</p>

                        <Box
                            sx={{
                                display: "flex",
                                flexDirection: "column",
                                gap: "1rem",
                            }}
                        >
                            <Box
                                sx={{
                                    border: "1px solid white",
                                    display: "flex",
                                    margin: "auto",
                                }}
                            >
                                {capacity && (
                                    <Box>
                                        {Array(capacity)
                                            .fill()
                                            .map((_, index) => (
                                                <BoyRounded
                                                    key={capacity}
                                                    style={{
                                                        color: "var(--color-white)",
                                                        fontSize: "2rem",
                                                    }}
                                                />
                                            ))}
                                    </Box>
                                )}
                                ;
                            </Box>

                            <Stack>
                                <p className="title secondary-light">
                                    $ {price}
                                </p>

                                <p className="parraph secondary-light">
                                    person per night
                                </p>
                                <Buttons data={"See more info"} />
                            </Stack>

                            <Box sx={{}}>
                                <IconButton size="sm" color="neutral">
                                    <Favorite />
                                </IconButton>

                                <IconButton size="sm" color="neutral" sx={{}}>
                                    <CreateNewFolder />
                                </IconButton>
                            </Box>
                        </Box>
                    </Box>
                </Link>
            </CardCover>
        </Card>
    );
};
