              <GuestsSelect 
              totalGuests={totalGuests}          
              setTotalGuests={setTotalGuests}/>
          </Select>
          {totalGuests &&
            <FormHelperText>{totalGuests}</FormHelperText>}
        </FormControl>


      <Button onClick={handleClick}>Aplicar</Button>

    </>
  );
}


