import { Stack } from "@mui/material";
import { Container, Typography } from "@mui/material";
import { Box } from "@mui/material";
import React from "react";
import { useRoomContext } from "../../context/RoomProvider";
import { RoomItem } from "./RoomItem";

export const RoomItemContainer = ({}) => {
    const { rooms, isList, setIsList } = useRoomContext();

    return (
        <Container
            sx={{
                display: "flex",
                width: "100%",
                height: "80vh",
                justifyContent: "center",
                alignItems: "center" }}>

            <Box sx={{ display: "flex", width: "100%" }}>
                <Stack
                    sx={{
                        gap: 4,
                    }}>
                    <p className="subtitle secondary"> Discover our rooms </p>
                    <p className="title primary"> Choose your room </p>
                    <p className="parraph primary">
                                the Best place to enjoy life place to enjoy life place
                                to enjoy life place to enjoy life place to enjoy life
                                place to enjoy life place to enjoy life place to enjoy
                                life place to enjoy life.</p>
                </Stack>

                {/* scroll */}
                <Container
                    sx={{
                        overflowX: "auto",
                        height: "85%",
                        maxHeight: "70vh"}} >
                    <Box
                        sx={{
                            display: "flex",
                            gap: "1.5rem",
                            minHeight: "60vh",
                        }}
                    >
                        {rooms &&
                            rooms.map((room) => (
                                <Box key={rooms.title}>
                                    {room && (
                                        <RoomItem
                                            isList={isList}
                                            setIsList={setIsList}
                                            rooms={room}
                                        />
                                    )}
                                </Box>
                            ))}
                    </Box>
                </Container>
            </Box>
        </Container>
    );
};
