import Button from "@mui/material/Button";


import { styled } from '@mui/material/styles';
import Stack from '@mui/material/Stack';



const BookButtonStyled = styled(Button)({
  boxShadow: 'none',
  textTransform: 'none',
  fontSize: 16,
  padding: '6px 12px',
  border: '1px solid',
  lineHeight: 1.5,
  backgroundColor: '#b47201',  

  '&:hover': {
    backgroundColor: '#b47201a4',
    borderColor: 'none',
    boxShadow: '0 0 0 0.2rem #241c027f'
  },
  '&:active': {
    boxShadow: 'none',
    backgroundColor: '#0062cc',
    borderColor: '#005cbf',
  },
  '&:focus': {
    boxShadow: '0 0 0 0.2rem #241c027f',
  },
});


export const Buttons = ({ data }) => {
    return (
        <>
            <BookButtonStyled
                variant="contained"
                sx={{
                    my: 2,
                    display: "block",
                }}
                onClick={() => {
                    alert("clicked");
                }}>
                {data}
            </BookButtonStyled>

        </>
    );
};



