import { Stack } from "@mui/material";
import { Container, Typography } from "@mui/material";
import { Box } from "@mui/material";
import React from "react";
import { useRoomContext } from "../../context/RoomProvider";
import { RoomItem } from "./RoomItem";

export const RoomItemContainer = ({}) => {
    const { rooms, isList, setIsList } = useRoomContext();

    return (
        <Container sx={{ display: "flex", height: "50vh", justifyContent: "center", alignItems: "center"}}>
             <Box sx={
                { display: "flex", 
             }}>
                 <Stack>
                     <p className="subtitle"> Discover our rooms </p>
                     <p className="title"> Choose your room </p>
                 </Stack>

                 {/* scroll */}
                 <Container
                     sx={{
                         overflowX: "scroll",
                     }}
                 >
                     <Box
                         sx={{
                             display: "flex",
                             gap: "1.5rem",

                         }}>
                         {rooms &&
                             rooms.map((room) => (
                                 <Box key={rooms.title}>
                                     {room && (
                                         <RoomItem
                                             isList={isList}
                                             setIsList={setIsList}
                                             rooms={room}
                                         />
                                     )}
                                 </Box>
                             ))}
                     </Box>
                 </Container>
             </Box>
       </Container>
    );
};
