import { Typography, Box, Stack, Button } from '@mui/material'
import React, { useState } from 'react'
import 'react-date-picker/dist/DatePicker.css';
import 'react-calendar/dist/Calendar.css';
import DatePicker from 'react-date-picker';
import { CalendarToday } from '@mui/icons-material';
import { CalendarMonth } from '@mui/icons-material';
import FormHelperText from '@mui/material/FormHelperText';




import Grid from '@mui/material/Grid';
import Slider from '@mui/material/Slider';
import MuiInput from '@mui/material/Input';
import { BoyRounded } from '@mui/icons-material';
import { styled } from '@mui/material/styles';


const Input = styled(MuiInput)`
  width: 42px;
`;


export const SearchBar = () => {
    const [startDate, setStartDate] = useState(false)
    const [endDate, setEndDate] = useState(false)

    const [adults, setAdults] = useState(2);
    const [kids, setKids] = useState(1);
    const [totalGuests , setTotalGuests ] = useState()


  const handleAdultSliderChange = (e, value) => {
    setAdults(value);
  };

  const handleAdultInputChange = (e) => {
    setAdults(e.target.value === '' ? '' : Number(e.target.value));
  };

  const handleAdultBlur = () => {
    if (adults < 0) {
        setAdults(0);
    } else if (setAdults > 5) {
        setAdults(5);
    }
  };

  const handlekidsSliderChange = (e, value) => {
    setKids(value);
  };

  const handlekidsInputChange = (event) => {
    setKids(event.target.value === '' ? '' : Number(event.target.value));
  };

  const handlekidsBlur = () => {
    if (adults < 0) {
        setKids(0);
    } else if (adults > 5) {
        setKids(5);
    }
  };

  const handleClick = () => {
    const total = adults + kids
    setTotalGuests(total)
  }


  return (
    <div>
        <Box>
            <Typography>
                <CalendarToday />
                CheckIn
            </Typography>
            <DatePicker 
                value={startDate}
                className={"w-full h-full"}
                selected={startDate}
                placeholder="Check in"
                onChange={(date) => setStartDate(date)}/>
        </Box>



        <Box>
            <Typography>
                <CalendarMonth />
                CheckOut
            </Typography>
            <DatePicker 
                value={endDate}
                className={"w-full h-full"}
                selected={endDate}
                placeholder="Check Out"
                onChange={(date) => setEndDate(date)}/>
        </Box>


        <Stack>
          <Box sx={{ width: 150 }}>
            <Typography id="input-slider" variant="caption">
                        Adultos
            </Typography>

          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <BoyRounded />
            </Grid>

            <Grid item xs>
              <Slider
                id="adult"
                size="small"
                value={adults ? adults : 0}
                onChange={handleAdultSliderChange}
                step={1}
                min={1}
                max={5}
                aria-labelledby="input-slider"
              />
            </Grid>

            <Grid item>
              <Input
                value={adults}
                size="small"
                onChange={handleAdultInputChange}
                onBlur={handleAdultBlur}
                inputProps={{
                  step: 1,
                  min: 0,
                  max: 5,
                  type: 'number',
                  'aria-labelledby': 'input-slider',
                }}
              />
            </Grid>

          </Grid>
          </Box>
        </Stack>



        <Stack>
            <Box sx={{ width: 150 }}>
                <Typography id="input-slider" variant="caption">
                            Niños
                </Typography>

              <Grid container spacing={2} alignItems="center">
                <Grid item>
                  <BoyRounded />
                </Grid>

                <Grid item xs>
                  <Slider
                    id="kids"
                    size="small"
                    value={kids ? kids : 0}
                    onChange={handlekidsSliderChange}
                    step={1}
                    min={0}
                    max={5}
                    aria-labelledby="input-slider"
                  />
                </Grid>

                <Grid item>
                  <Input
                    value={kids}
                    size="small"
                    onChange={handlekidsInputChange}
                    onBlur={handlekidsBlur}
                    inputProps={{
                      step: 1,
                      min: 0,
                      max: 5,
                      type: 'number',
                      'aria-labelledby': 'input-slider',
                    }}
                  />
                </Grid>

              </Grid>
            </Box>
        </Stack> 


        {totalGuests &&
            <FormHelperText> <BoyRounded /> {adults} Adults - {kids} Kids</FormHelperText>}



        <Button onClick={handleClick}>Aplicar</Button>





    </div>
  )
}
