import { Typography, Box, Stack } from '@mui/material'
import React, { useState } from 'react'
import 'react-date-picker/dist/DatePicker.css';
import 'react-calendar/dist/Calendar.css';
import DatePicker from 'react-date-picker';
import { CalendarToday } from '@mui/icons-material';
import { CalendarMonth } from '@mui/icons-material';



import Grid from '@mui/material/Grid';
import Slider from '@mui/material/Slider';
import MuiInput from '@mui/material/Input';
import { BoyRounded } from '@mui/icons-material';
import { styled } from '@mui/material/styles';


const Input = styled(MuiInput)`
  width: 42px;
`;


export const SearchBar = () => {
    const [startDate, setStartDate] = useState(false)
    const [endDate, setEndDate] = useState(false)

    const [adult, setAdult] = useState(2);
    const [kids, setKids] = useState(1);
    const [adults, setAdults] = useState()


  const handleAdultSliderChange = (e, value) => {
    console.log("hanldechange de adult change")

    setAdult(value);
  };

  const handleAdultInputChange = (e) => {
    console.log("hanldechange de select")

    setAdult(e.target.value === '' ? '' : Number(e.target.value));
  };

  const handleAdultBlur = () => {
    if (adult < 0) {
        setAdult(0);
    } else if (adult > 5) {
        setAdult(5);
    }
  };

  const handlekidsSliderChange = (e, value) => {
    setKids(value);
  };

  const handlekidsInputChange = (event) => {
    setKids(event.target.value === '' ? '' : Number(event.target.value));
  };

  const handlekidsBlur = () => {
    if (adult < 0) {
        setKids(0);
    } else if (adult > 5) {
        setKids(5);
    }
  };


  return (
    <div>
        <Box>
            <Typography>
                <CalendarToday />
                CheckIn
            </Typography>
            <DatePicker 
                value={startDate}
                className={"w-full h-full"}
                selected={startDate}
                placeholder="Check in"
                onChange={(date) => setStartDate(date)}/>
        </Box>



        <Box>
            <Typography>
                <CalendarMonth />
                CheckOut
            </Typography>
            <DatePicker 
                value={endDate}
                className={"w-full h-full"}
                selected={endDate}
                placeholder="Check Out"
                onChange={(date) => setEndDate(date)}/>
        </Box>


        <Stack>
          <Box sx={{ width: 150 }}>
            <Typography id="input-slider" variant="caption">
                        Adultos
            </Typography>

          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <BoyRounded />
            </Grid>

            <Grid item xs>
              <Slider
                id="adult"
                size="small"
                value={adult ? adult : 0}
                onChange={handleAdultSliderChange}
                step={1}
                min={1}
                max={5}
                aria-labelledby="input-slider"
              />
            </Grid>

            <Grid item>
              <Input
                value={adult}
                size="small"
                onChange={handleAdultInputChange}
                onBlur={handleAdultBlur}
                inputProps={{
                  step: 1,
                  min: 0,
                  max: 5,
                  type: 'number',
                  'aria-labelledby': 'input-slider',
                }}
              />
            </Grid>

          </Grid>
          </Box>
        </Stack>



        <Stack>
            <Box sx={{ width: 150 }}>
                <Typography id="input-slider" variant="caption">
                            Niños
                </Typography>

              <Grid container spacing={2} alignItems="center">
                <Grid item>
                  <BoyRounded />
                </Grid>

                <Grid item xs>
                  <Slider
                    id="kids"
                    size="small"
                    value={kids ? kids : 0}
                    onChange={handlekidsSliderChange}
                    step={1}
                    min={0}
                    max={5}
                    aria-labelledby="input-slider"
                  />
                </Grid>

                <Grid item>
                  <Input
                    value={kids}
                    size="small"
                    onChange={handlekidsInputChange}
                    onBlur={handlekidsBlur}
                    inputProps={{
                      step: 1,
                      min: 0,
                      max: 5,
                      type: 'number',
                      'aria-labelledby': 'input-slider',
                    }}
                  />
                </Grid>

              </Grid>
            </Box>
        </Stack> 



    </div>
  )
}
