import { Box } from '@mui/material'
import React, { useState } from 'react'
import { itemView } from '../utils/itemView'


export const FilterIcons = () => {

  const [select, setSelect] = useState("")


  const handleClick = (e, name) => {
     setSelect(name)
    console.log(select)
    //if viewType == "el"
  }
  return (
    <Box sx={{display: "flex", gap: "1rem", justifyContent: "end", }}>
        {itemView.map((view) => (
                <ItemView onClick={handleClick} icon={view.icon} name={view.name} key={view.name} />
           ))} 
    </Box>

  )
}


const ItemView = ({ name, icon, handleClick, key }) => {
  return (
    <div onClick={() => handleClick(name)}>
      <span>{name}</span>
      <span>{icon}</span>
    </div>
  );
}
