import { Typography, Box, Stack, Button } from "@mui/material";
import React, { useState } from "react";

import FormHelperText from "@mui/material/FormHelperText";

import Grid from "@mui/material/Grid";
import Slider from "@mui/material/Slider";
import MuiInput from "@mui/material/Input";
import { BoyRounded } from "@mui/icons-material";
import { styled } from "@mui/material/styles";
import { Container } from "@mui/material";

import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { useRoomContext } from "../../context/RoomProvider";

import { searchDates } from "../../utils/searchDates"
import { TextField } from "@mui/material";
import { GifBoxSharp } from "@mui/icons-material";
import { Divider } from "@mui/material";
import { InputLabel } from "@mui/material";

const Input = styled(MuiInput)`
    width: 42px;
`;

export const SearchBar = ({
    handleSliderChange,
    handleInputChange,
    handleBlur,
    setStartDate,
    setEndDate,
}) => {

    const { adults, kids, handleClick } = useRoomContext();


    const handleChange = (e, date) => {
        const id = e.target.id
        if (id === "start") {
            setStartDate(date);
        } else if (id === "end"){
            setEndDate(date);
        }
    };


    return (
        <Container sx={{}}>
            <Box
                sx={{
                    display: "flex",
                    justifyContent: "space-around",
                    alignItems: "center",
                    backgroundColor: "#f0efed",
                    boxShadow: "10px 10px 35px -21px rgba(69,68,69,1)",
                    padding: "2rem",
                    borderRadius: "10px",
                }}
            >
                <Box>
                    <InputLabel>Check In</InputLabel>
                    <input
                        type="date"
                        label="Check In"
                        id="start"
                        name="trip-start"
                        value="2023-08-17"
                        onChange={(date) => setStartDate(date)}
                        min="2023-01-01"
                        max="2030-12-31"
                    />
                </Box>
                {
                    searchDates.map((elem) => 
                    elem && 

                    <>

                    <Box>
                    <InputLabel>{elem.label}</InputLabel>
                    <input
                        type={elem.type}
                        label={elem.label}
                        id={elem.id}
                        name={elem.name}
                        value={elem.value}
                        onChange={(date) => 
                            setStartDate(date)}
                        min={elem.min}
                        max={elem.max}
                    />
                </Box>               
                <Divider orientation="vertical" flexItem />
                </>

                    )

                }







                <Box>
                    <InputLabel>Check Out</InputLabel>

                    <input
                        type="date"
                        id="end"
                        label="Check out"
                        name="trip-start"
                        defaultValue="2023-08-17"
                        value="2023-08-17"
                        onChange={(date) => setEndDate(date)}
                        min="2023-01-01"
                        max="2030-12-31"
                    />
                </Box>

                <Divider orientation="vertical" flexItem />

                <Box>
                    <InputLabel>Adults</InputLabel>
                    <Box sx={{ display: "flex" }}>
                        <Box>
                            <BoyRounded />
                        </Box>

                        <Box>
                            <input
                                type="range"
                                id="adults"
                                name="adult-guests"
                                value={adults}
                                onChange={handleSliderChange}
                                step={1}
                                min={1}
                                max={5}
                                style={{ width: "100px" }}
                            />
                        </Box>

                        <Box>
                            <Input
                                id="adults"
                                value={adults}
                                size="small"
                                onChange={handleInputChange}
                                onBlur={handleBlur}
                                inputProps={{
                                    step: 1,
                                    min: 0,
                                    max: 5,
                                    type: "number",
                                    "aria-labelledby": "input-slider",
                                }}
                            />
                        </Box>
                    </Box>
                </Box>

                <Divider orientation="vertical" flexItem />

                <Box>
                    <InputLabel>Kids</InputLabel>

                    <Box sx={{ display: "flex" }}>
                        <Box>
                            <BoyRounded />
                        </Box>

                        <Box>
                            <input
                                style={{ width: "100px" }}
                                type="range"
                                id="kids"
                                name="kids-guests"
                                value={kids}
                                onChange={handleSliderChange}
                                step={1}
                                min={1}
                                max={5}
                            />
                        </Box>

                        <Box>
                            <Input
                                value={kids}
                                size="small"
                                id="kids"
                                onChange={handleInputChange}
                                onBlur={handleBlur}
                                inputProps={{
                                    step: 1,
                                    min: 0,
                                    max: 5,
                                    type: "number",
                                    "aria-labelledby": "input-slider",
                                }}
                            />
                        </Box>
                    </Box>
                </Box>

                <Box sx={{ width: 150 }}>
                    <Button
                        onClick={handleClick}
                        variant="contained"
                        color="primary"
                    >
                        Check Availability
                    </Button>
                </Box>
            </Box>
        </Container>
    );
};

