import React from 'react';

import { Typography, Container, Box } from '@mui/material';

const bgImg = "https://images.pexels.com/photos/6129991/pexels-photo-6129991.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"

export const Intro = () => {

    return (
        <Container sx={{
           padding: { xs: 0, md: 0 }}}>
            {/* img */}
            <Box sx={{
                    zIndex: 0,
                    overflow: "hidden",
                    position: "absolute",
                    height: "100vh",
                    width: "100%",
                }}>
                    <img src={ bgImg } alt="" sx={{
                    position: "absolute",

                }} />

            </Box>

                {/* text box */}
                <Box sx={{
                    zIndex: 999,
                    position: "absolute",
                    top: "25%",
                    left: "35%",
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "center",
                    padding: "1rem",
                    gap: "1rem",
                    color: "white",
                    backdropFilter: "blur(10px)",
                    backgroundColor: "rgba(238, 236, 236, 0.155)",
                    borderRadius: "10px"
                }}>
                    <Typography 
                        variant="subtitle1"
                        sx={{borderBottom: "2px solid white"}}
                        >W E L C O M E </Typography>
                    <Typography
                        variant="h3"
                        >LAS SALINAS </Typography>
                    <Typography
                        variant="subtitle2"
                        >Your favorite choice </Typography>

                </Box>
        </Container>
    )
}
