import { useState } from "react";
import { useLocation } from "react-router-dom";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Container from "@mui/material/Container";
import { footer } from "../../utils/footer";
import { Link, NavLink } from "react-router-dom";

export const Footer = () => {
  const location = useLocation();


  return (
      <Container sx={{backgroundColor:"var(--color-primary)"}} >
        <Toolbar disableGutters>

        <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={2}>
        <Grid item xs={6} md={8}>
          <Item>xs=6 md=8</Item>
        </Grid>
        <Grid item xs={6} md={4}>
          <Item>xs=6 md=4</Item>
        </Grid>
        <Grid item xs={6} md={4}>
          <Item>xs=6 md=4</Item>
        </Grid>
        <Grid item xs={6} md={8}>
          <Item>xs=6 md=8</Item>
        </Grid>
      </Grid>
    </Box>
          <Box
            sx={{
              flexGrow: 1,
              display: { xs: "none", md: "flex" },
              gap: "2rem",
              fontSize: "0.7rem",
              textTransform: "capitilize",
              justifyContent: "center",
            }}
          >
            {footer.map((feet) => (
              <NavLink
                key={feet.id}
                style={{
                  color:
                    location.pathname === feet.title ? "#fff" : "var(--color-secondary)",
                }}
              >
                {feet.title}
              </NavLink>
            ))}
          </Box>


        </Toolbar>
      </Container>
  );
};
