import { Box } from "@mui/material";
import { Stack } from "@mui/material";
import { Typography } from "@mui/material";
import { Toolbar } from "@mui/material";
import { Container } from "@mui/material";
import { features } from "../../utils/features";

export const Features = () => {
    return (
        <Container sx={{ display: "flex", height: "100vh", justifyContent: "center", alignItems: "center"}}>
            <Stack
                sx={{
                    display: "flex",
                    alignItems: "center",
                    gap: "2rem"
                }}>

                <p className="subtitle">
                Enjoy all benefits while you stay</p>
                <p className="title">
                    Our services
                </p>

                <Box
                    sx={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        flexWrap: "wrap",
                    }}>

                    {features.map((feature) => (
                        <Feature key={feature.id} data={feature} />
                    ))}
                </Box>
            </Stack>
        </Container>
    );
};

<Box
      sx={{
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "center",
      }}
    >
      {features.map((feature, index) => (
        <Box
          key={index}
          sx={{
            flex: "0 0 25%",
            maxWidth: "25%",
            marginBottom: "16px",
          }}
        >
          <Feature data={feature} />
        </Box>
      ))}
    </Box>

const Feature = ({ data }) => {
    const { id, icon, title, description } = data;
    return (
        <Stack
            sx={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                gap: "0.5rem",
                color: "#4f4e4e",
            }}
        >
            <Box>{icon}</Box>
            <Typography
                sx={{ borderBottom: "2px solid black" }}
                variant="subtitle2"
            >
                {title}
            </Typography>
            <Typography
                sx={{
                    textAlign: "center",
                }}
                variant="caption"
            >
                {description}
            </Typography>
        </Stack>
    );
};
