import React from "react";

import AspectRatio from "@mui/joy/AspectRatio";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import Chip from "@mui/joy/Chip";
import IconButton from "@mui/joy/IconButton";
import Typography from "@mui/joy/Typography";
import Favorite from "@mui/icons-material/Favorite";
import CreateNewFolder from "@mui/icons-material/CreateNewFolder";
import { BoyRounded } from "@mui/icons-material";
import { Button } from "@mui/material";
import { Stack } from "@mui/material";
import FormHelperText from "@mui/material/FormHelperText";
import { Link } from "react-router-dom";
import { CardContent } from "@mui/material";

export const RoomItem = ({ rooms }) => {
    const title = rooms.title;
    const capacity = rooms.capacity;
    const amenities = rooms.amenities;
    const allows = rooms.allows;
    const donts = rooms.donts;
    const description = rooms.description;
    const price = rooms.price;
    const imgUrl = rooms.imgUrl;

    return (
        <Link to={`/room/${title}`}>
            <Card
                sx={{
                    width: 300,
                    bgcolor: "initial",
                    boxShadow: "none",
                    "--Card-padding": "0px",
                }}
            >
                <Box sx={{ position: "relative" }}>
                    <AspectRatio ratio="3/4">
                        <figure>
                            <img src={imgUrl} loading="lazy" alt={title} />
                        </figure>
                    </AspectRatio>

                    <CardContent
                        className="gradient-cover"
                        sx={{
                            "&:hover, &:focus-within": {
                                opacity: 1,
                            },
                            opacity: 0,
                            transition: "0.1s ease-in",
                            background:
                                "linear-gradient(0deg, rgba(2,0,36,0.8379551649761467) 0%, #033e4a34 100%); ",
                        }}
                    >
                        {/* The first box acts as a container that inherits style from the CardCover */}
                        <Box
                            sx={{
                                display: "flex",
                                flexDirection: "column",
                                top: "1rem",
                            }}
                        >


                            <Box
                                sx={{
                                    display: "flex",
                                    flexDirection: "column",
                                    justifyContent: "end",
                                    alignItems: "end",
                                    gap: "1rem",
                                    alignSelf: "end",
                                    padding: "3rem 2rem",
                                }}
                            >
                                <Typography
                                    variant="h6"
                                    sx={{
                                        color: "#fff",
                                        alignSelf: "start",
                                        padding: "3rem 2rem",
                                        fontWeight: "bold",
                                        letterSpacing: 1,
                                    }}
                                >
                                    {title}
                                </Typography>

                                <Chip
                                    variant="outlined"
                                    color="neutral"
                                    size="sm"
                                    sx={{
                                        borderRadius: "sm",
                                        ml: "auto",
                                        display: "flex",
                                    }}
                                >
                                    {capacity && (
                                        <div>
                                            {Array(capacity)
                                                .fill()
                                                .map((_, index) => (
                                                    <BoyRounded
                                                        style={{
                                                            color: "#fff",
                                                        }}
                                                    />
                                                ))}
                                        </div>
                                    )}
                                    ;
                                </Chip>
                            </Box>

                            {/* PRICE Y RESERVATION */}
                            <Stack>
                                <Typography
                                    sx={{ fontSize: "5rem", color: "#fff" }}
                                >
                                    $ {price}
                                </Typography>

                                <FormHelperText
                                    sx={{ color: "#fff", alignSelf: "end" }}
                                >
                                    {" "}
                                    person per night
                                </FormHelperText>

                                <Button variant="contained" color="primary">
                                    RESERVATION
                                </Button>
                            </Stack>
                        </Box>
                    </CardContent>
                </Box>
            </Card>
        </Link>
    );
};

