import { Stack } from "@mui/material";
import { Container, Typography } from "@mui/material";
import { Button } from "@mui/material";
import { Box } from "@mui/material";
import React from "react";
import { useRoomContext } from "../../context/RoomProvider";
import { RoomItem } from "./RoomItem";

export const RoomItemContainer = ({}) => {
    const { rooms, isList, setIsList } = useRoomContext();

    return (
        <>
            <Container
                sx={{
                    display: "flex",
                    width: "100%",
                    height: "80vh",
                    justifyContent: "center",
                    alignItems: "center" }}>



                <Box sx={{ display: "flex", width: "100%" }}>
                    <Stack spacing={1}
                        sx={{ width: "40%" }}>
                        <p className="subtitle secondary"> Discover our rooms </p>
                        <p className="title primary"> Choose your room </p>
                        <p className="parraph primary">
                                    the Best place to enjoy life place to enjoy life place
                                    to enjoy life place to enjoy.</p>
                    </Stack>

                    {/* scroll */}
                    <Container
                        sx={{
                            overflowX: "scroll",
                            scrollBehavior: "smooth",
                            scrollSnapType: "x mandatory",
                            scrollbarWidth: "none",
                            height: "100%",
                            maxHeight: "70vh"}} >

                        <Box
                            sx={{
                                display: "flex",
                                gap: "1.5rem",
                                minHeight: "60vh",
                                position: "relative"}}
                                >

                            <Box sx={{
                                position: "absolute",
                                top: "50%",
                                justifyContent: "space-between",
                                display: "flex",
                                left: "1rem",
                                right: "2rem",
                            transform: "translateY(-50%)",
                            zIndex: 2000 }} > 

                            <Button sx={{position: "absolute", zIndex: 10000}} >Bbbbbbb</Button>
                            <Button sx={{position: "absolute", zIndex: 10000}} >Bbbbbbb</Button>

                            </Box>

                            {rooms &&
                            rooms.map((room) => (
                            <Box key={rooms.title}>
                                        {room && (
                                            <RoomItem
                                                isList={isList}
                                                setIsList={setIsList}
                                                rooms={room}
                                            />
                                        )}
                            </Box>))}
                        </Box>                   
                    </Container>
                </Box>



            </Container>
            <Box sx={{
                            position: "absolute",
                            top: "50%",
                            justifyContent: "space-between",
                            display: "flex",
                            left: "1rem",
                            right: "2rem",
                        transform: "translateY(-50%)",
                        zIndex: 2000 }} > 

                        <Button sx={{position: "absolute", zIndex: 10000}} >Bbbbbbb</Button>
                        <Button sx={{position: "absolute", zIndex: 10000}} >Bbbbbbb</Button>

            </Box>
        </>
    );
};
