//top

const TopText = {
    backgroundColor: "#104a00",
    color: "white",
    fontSize: "0.8rem",
    fontWeight: 700,
    textTransform: "uppercase",
    letterSpacing: "5px",
    padding: "0.4rem 0.5rem",
    marginBottom: "0.3rem",
};

const Title = {
    color: "#1c1c1c",
    fontFamily: "EB Garamond, serif",
    fontSize: "3rem",
    fontWeight: 700,
    textTransform: "capitalize",
};

const Subtitle = {
    color: "#1c1c1c",
    fontSize: "1rem",
    fontWeight: 400,
    textTransform: "lowercase",
    textAlign: "center",
    margin: 0,
};

export { TopText, Title, Subtitle };
